sqlMap.select:
SELECT s.BUSI_ID busiId,s.BUSI_CODE busiCode,s.BUSI_NAME busiName,p.product_Name pName,CONCAT(DATE_FORMAT(s.EFF_DATE,'%Y-%m-%d'),'~',DATE_FORMAT(s.EXP_DATE,'%Y-%m-%d')) pDate from settle_sp_busi_def s,product P
s.PARTNER_CODE=#partnerCode# s.BUSI_CODE=#busiCode# s.SERV_CODE=#servCode# s.STATUS_CD=#statusCd#
and s.BUSI_CODE = p.product_id;
