sqlMap.select:
SELECT count(*) FROM (SELECT ROWNUM RECNO, TECH_IMPL_ID,TECH_IMPL_NAME,COMPONENT_ID,NAME,CODE,comm_pro_cd,usealbe_state,laest_time FROM (select a.TECH_IMPL_ID,a.TECH_IMPL_NAME,a.COMPONENT_ID,b.NAME,b.CODE,a.COMM_PRO_CD,a.USEALBE_STATE,a.LAEST_TIME from TECH_IMPL a,COMPONENT b where a.component_id=b.component_id AND a.TENANT_ID = #tenantId# AND b.TENANT_ID = #tenantId#
b.CODE=#componentCode# b.NAME like '%'||#componentName#||'%' b.org_id=#orgId# a.comm_pro_cd=#commProCd# a.tech_impl_name like '%'||#techImplName#||'%' a.USEALBE_STATE=#usealbeState#
) ORDER BY TECH_IMPL_ID DESC)

sqlMap.insert:
INSERT INTO TECH_IMPL (REG_TIME,LAEST_TIME,TECH_IMPL_CODE,TENANT_ID
TECH_IMPL_NAME TECH_IMP_CON_PO_ID COMPONENT_ID COMM_PRO_CD USEALBE_STATE
) VALUES (SYS_EXTRACT_UTC(current_timestamp)+0,SYS_EXTRACT_UTC(current_timestamp)+0,sys_guid(),#tenantId#
#techImplName# #techImpConPoId# #componentId# #commProCd# #usealbeState#
) where TECH_IMPL.TECH_IMPL_ID=#techImplId#
