sqlMap.select:
SELECT s.BUSI_ID busiId,s.BUSI_CODE busiCode,s.BUSI_NAME busiName,p.product_Name pName,(TO_CHAR(s.EFF_DATE,'yyyy:MM:dd')||'~'||TO_CHAR(s.EXP_DATE,'yyyy:MM:dd')) pDate from settle_sp_busi_def s,product P
s.PARTNER_CODE=#partnerCode# s.BUSI_CODE=#busiCode# s.SERV_CODE=#servCode# s.STATUS_CD=#statusCd#
and s.BUSI_CODE = p.product_id;
